import React from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import "./UserPage.css";

function UserPage(props) {
    let cardList;
    let collectionList;

    const [viewMode, setViewMode] = useState(3)

    const handleViewCards=()=> {
        setViewMode(1);
    }
    const handleViewCollections=()=> {
        setViewMode(1);
    }
    const handleViewBoth=()=> {
        setViewMode(1);
    }



    if(props.cards){
        cardList = props.cards.map((card, index)=>{
            return(
              <Card className = "card" key= {index} bg= 'Info' text = 'dark' style={{ width: '18rem' }}>
                 <Card.Header as="h5"><Card.Title>{card.cardName}</Card.Title></Card.Header>
                 <Card.Body>
                     <Card.Text>
                     {card.description}
                     </Card.Text>
                     <Button variant="secondary">Go somewhere</Button>
                 </Card.Body>
             </Card>
             )
         })
    }

    if(props.collections){
        collectionList = props.collections.map((collection, index)=>{
            return(
              <Card className = "card"  key= {index} bg= 'Info' text = 'dark' style={{ width: '18rem' }}>
                 <Card.Img vaiant="top" src="../logo.svg" />
                 <Card.Body>
                     <Card.Title>{collection.colName}</Card.Title>
                     
                     <Button variant="secondary">Go somewhere</Button>
                 </Card.Body>
             </Card>
             )
         })
    }
    
    return (
        <div className='userPage'>
            <div className='header'>
                <Navbar bg="light" expand="lg">
                    <Container>
                        <Navbar.Brand href="#home">Main Menu</Navbar.Brand>
                        <Navbar.Toggle aria-controls="basic-navbar-nav" />
                        <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="me-auto">
                            <Nav.Link href="#home">Home</Nav.Link>
                            <NavDropdown title="View Options" id="basic-nav-dropdown">
                            <NavDropdown.Item href="#action/3.1" onClick={handleViewCards}>Cards</NavDropdown.Item>
                            <NavDropdown.Item href="#action/3.2" onClick={handleViewCollections}>
                                Collections
                            </NavDropdown.Item>
                            <NavDropdown.Item href="#action/3.3" onClick={handleViewBoth}>Both</NavDropdown.Item>
                           
                            </NavDropdown>
                        </Nav>
                        </Navbar.Collapse>
                    </Container>
                </Navbar>
            </div>
            <div className='body'>
                <p>Your Cards</p>

                <div className='cardList'> {cardList}</div>

                <p>Your Collections</p>
                <div className='cardList'> {collectionList}</div>

            </div>
           

        </div>
    );
}

export default UserPage;
