import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

import { Button, Form } from 'react-bootstrap'
import AdaptativeModal from './AdaptativeModal/AdaptativeModal';


function LoginModal(props) {
    /*
    Para o caso de querermos footer:
      const closeButtonText = "Close";
      const loginButtonText = "Login";
      const footer = true;
    */

    
   const email= "", pass = "";
  
    const modalTitle= "Login";
    const footer = false;
    
    const handleLoginSubmit = ()=>{
        this.props.handleLogin(email, pass)
    }

    const modalProps = {
      modalTitle,
      footer
    } 

    return (
        
        <AdaptativeModal {...props} {...modalProps}>
            <Form>
            <Form.Group className="mb-3" controlId="username">
                <Form.Label>Username</Form.Label>
                <Form.Control type="email" placeholder="Enter username" ref={(ref) => { this.email = ref }}  />
                <Form.Text className="text-muted">
                Login through real name is not supported as it may not be unique. 
                </Form.Text>
            </Form.Group>

            <Form.Group className="mb-3" controlId="password">
                <Form.Label>Password</Form.Label>
                <Form.Control type="password" placeholder="Password" ref={(ref) => { this.pass = ref }} />
            </Form.Group>

            {props.error ? <Form.Text className="text-muted">
                Invalid Username or Password. Try again.
            </Form.Text>: <></>}
            
            <Button variant="primary" type="submit" onClick={handleLoginSubmit}>
                Login
            </Button>
            </Form>

        </AdaptativeModal>
    );
}

export default LoginModal;
