import Card from 'react-bootstrap/Card';

function DisplayCard(props){

    const {isCard} = props;

    if(isCard){
        const {card} = props;
        return (
            <Card className = "card" key= {index} bg= 'Info' text = 'dark' style={{ width: '18rem' }}>
                <Card.Header as="h5">
                    <Card.Title>{card.cardName}</Card.Title>
                    <Card.Subtitle className="subtitle">Collection: {getCardCol(card.collectionId)}</Card.Subtitle>
                </Card.Header>
                <Card.Body>
                    <Card.Text>{card.description}</Card.Text>
                </Card.Body>
                <Card.Footer>
                    <ButtonGroup aria-label="First group">
                        <Button  size = "sm" variant="secondary">Edit</Button>
                        <Button  size = "sm" variant="secondary">Delete</Button>
                    </ButtonGroup>
                </Card.Footer>
            </Card>
            ) 
    }

   else{
    const {collection} = props;
    return (
        <Card className = "card"  key= {index} bg= 'Info' text = 'dark' style={{ width: '18rem' }}>
                <Card.Header as="h5">
                </Card.Header>
                 <Card.Img vaiant="top" src="../logo.svg" />
                 <Card.Body>
                     <Card.Title>{collection.colName}</Card.Title>
                 </Card.Body>
                 <Card.Footer>
                    <ButtonGroup aria-label="First group" >
                            <Button size = "sm" variant="light">Edit</Button>
                            <Button size = "sm" variant="outline-info" onClick={handleViewCardsModal}>View Cards</Button>
                            <Button size = "sm" variant="outline-secondary">Delete</Button>
                        </ButtonGroup>
                 </Card.Footer>
             </Card>
    )
   }
        
    

}

export default DisplayCard;
