import React, {useRef} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

import { Button, Form } from 'react-bootstrap'
import AdaptativeModal from './AdaptativeModal.js';
import DisplayCard from '../DisplayCards/DisplayCard.js';


function CardsModal(props) {
  
    const {cards, showModal, handleCloseModal} = props;

    const modalProps = {
      modalTitle,
      footer,
      handleClose: handleCloseModal,
      show: showModal,
    } 

    const displayCardProps = {
      isCards: true,
      cards
    }

    const modalTitle= "Collection "+ props.colName + " Cards";
    const footer = false;


    return (
        
        <AdaptativeModal {...props} {...modalProps}>
          <DisplayCard {...displayCardProps} />
        </AdaptativeModal>
    );
}

export default CardsModal;
