import React, {useRef} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

import AdaptativeModal from './AdaptativeModal.js';
import InfoDisplay from '../DisplayCards/InfoDisplay.js';


function CardsModal(props) {
  
    const {cards, viewCardsModal, handleViewCardsModal, colName} = props;
    const modalTitle= "Collection "+ colName + " Cards";
    const footer = false;

    const modalProps = {
      modalTitle,
      footer,
      handleClose: handleViewCardsModal,
      show: viewCardsModal,
    } 

    const displayCardProps = {
      isCards: true,
      colName
    }

    const cardsList = cards.map((card, index)=>{
      let cardProps = {
        ...displayCardProps,
        card,
        index
      }
      return <InfoDisplay {...displayCardProps} />

    })

    return (
        
        <AdaptativeModal {...modalProps}>
          {cardsList}
        </AdaptativeModal>
    );
}

export default CardsModal;
